import unittest
from task import shiftEncrypt
from task import matrixEncrypt

class TestSum(unittest.TestCase):

    def test_shiftEncrypt(self):
        self.assertEqual(shiftEncrypt("Hello World"), "Khoor Zruog", "Should be Khoor Zruog")

    def test_matrixEncrypt(self):
    	output = [[9.0, 6.0, 12.0, 7.0, 13.0, 8.0, 13.0, 9.0, 4.0, 8.0, 8.0, 6.0, 6.0, 11.0, 13.0, 5.0], [18.0, 19.0, 5.0, 22.0, 8.0, 13.0, 14.0, 10.0, 5.0, 22.0, 20.0, 20.0, 18.0, 27.0, 20.0, 12.0], [19.0, 14.0, 13.0, 21.0, 15.0, 17.0, 21.0, 18.0, 8.0, 22.0, 17.0, 15.0, 14.0, 27.0, 18.0, 14.0], [19.0, 14.0, 13.0, 21.0, 15.0, 17.0, 21.0, 18.0, 8.0, 22.0, 17.0, 15.0, 14.0, 27.0, 18.0, 14.0], [28.0, 25.0, 18.0, 27.0, 26.0, 24.0, 28.0, 20.0, 10.0, 36.0, 25.0, 21.0, 29.0, 43.0, 28.0, 16.0], [6.0, 6.0, 0.0, 7.0, 2.0, 7.0, 1.0, 0.0, 2.0, 6.0, 4.0, 2.0, 2.0, 9.0, 4.0, 0.0], [25.0, 22.0, 16.0, 20.0, 16.0, 15.0, 20.0, 12.0, 14.0, 27.0, 23.0, 27.0, 25.0, 31.0, 25.0, 14.0], [28.0, 25.0, 18.0, 27.0, 26.0, 24.0, 28.0, 20.0, 10.0, 36.0, 25.0, 21.0, 29.0, 43.0, 28.0, 16.0], [22.0, 17.0, 14.0, 16.0, 16.0, 17.0, 13.0, 3.0, 14.0, 17.0, 17.0, 16.0, 12.0, 24.0, 21.0, 5.0], [19.0, 14.0, 13.0, 21.0, 15.0, 17.0, 21.0, 18.0, 8.0, 22.0, 17.0, 15.0, 14.0, 27.0, 18.0, 14.0], [13.0, 10.0, 4.0, 18.0, 6.0, 10.0, 13.0, 10.0, 5.0, 14.0, 15.0, 14.0, 9.0, 18.0, 13.0, 12.0]]
    	testOutput = matrixEncrypt("Hello World")
    	for pair in zip(output, testOutput):
    		row = list(pair[0])
    		testRow = list(pair[1])
    		self.assertListEqual(row, testRow, 
    			"\nShould be: \n[[ 9.  6. 12.  7. 13.  8. 13.  9.  4.  8.  8.  6.  6. 11. 13.  5.], \n[18. 19.  5. 22.  8. 13. 14. 10.  5. 22. 20. 20. 18. 27. 20. 12.], \n[19. 14. 13. 21. 15. 17. 21. 18.  8. 22. 17. 15. 14. 27. 18. 14.], \n[19. 14. 13. 21. 15. 17. 21. 18.  8. 22. 17. 15. 14. 27. 18. 14.], \n[28. 25. 18. 27. 26. 24. 28. 20. 10. 36. 25. 21. 29. 43. 28. 16.], \n[ 6.  6.  0.  7.  2.  7.  1.  0.  2.  6.  4.  2.  2.  9.  4.  0.], \n[25. 22. 16. 20. 16. 15. 20. 12. 14. 27. 23. 27. 25. 31. 25. 14.], \n[28. 25. 18. 27. 26. 24. 28. 20. 10. 36. 25. 21. 29. 43. 28. 16.], \n[22. 17. 14. 16. 16. 17. 13.  3. 14. 17. 17. 16. 12. 24. 21.  5.], \n[19. 14. 13. 21. 15. 17. 21. 18.  8. 22. 17. 15. 14. 27. 18. 14.], \n[13. 10.  4. 18.  6. 10. 13. 10.  5. 14. 15. 14.  9. 18. 13. 12.]]")
 				
if __name__ == '__main__':
    unittest.main()